import osfrom flask import Flask, render_template, request, redirect, url_forimport sentimentapp = Flask(__name__)app.config['UPLOAD_FOLDER'] = 'uploads/'@app.route("/base")@app.route('/', methods=['GET', 'POST'])def base():    if request.method == 'POST':	    file = request.files['file']	    if file:              file.save(os.path.join(app.config['UPLOAD_FOLDER'], file.filename))              sentiment_score = sentiment.sentiment_analysis(file.filename)              return render_template('results.html', sentiment_score = sentiment_score)    else:        return render_template("base.html")@app.route('/results')def results():    return render_template("results.html")if __name__ == "__main__":	app.run(host="0.0.0.0")	app.debug = True